// src/components/Home.js
import React from 'react';
import { useHistory } from 'react-router-cli';

function Home() {
    const history = useHistory();

    const handleAddTask = () => {
        history.push('/add-task');
    };

    return (
        <div className="container mx-auto mt-10">
            <h1 className="text-3xl font-bold">Welcome to Task Manager</h1>
            <button onClick={handleAddTask} id="add-task-button" className="bg-amber-400 hover:bg-amber-700 text-white font-bold py-2 px-4 rounded mt-4">Add</button>
        </div>
    );
}

export default Home;
