import axios from 'axios';
import React, { useState } from 'react';
import { UserContext } from '../components/user_context';
import { useContext } from 'react';


const AddUser = () => {
    const { setUser } = useContext(UserContext);
    const [user, setUserLocal] = useState({
        email: '',
        password: '',
        first_name: '',
        last_name: '',
    });
    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});


  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({ ...prevUser, [name]: value }));
    setErrors((prevErrors) => ({ ...prevErrors, [name]: '' }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user.email || !user.password || !user.first_name || !user.last_name) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        email: !user.email ? 'Required' : '',
        password: !user.password ? 'Required' : '',
        first_name: !user.first_name ? 'Required' : '',
        last_name: !user.last_name ? 'Required' : '',
      }));
      return;
    }
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/users/add', user);
      setUser(response.data); // Update global user state
      console.log('Success:', response.data);
    } catch (error) {
      if (error.response) {
        console.error('Server Response:', error.response.data);
      } else {
        console.error('Axios Error:', error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    // ...
    <form onSubmit={handleSubmit}>
      <label>Email:</label>
      <br />
      <input
        className="rounded-md m-2 p-2 w-1/2"
        type="email"
        name="email"
        value={user.email}
        onChange={handleChange}
      />
      {errors.email && <div className="text-red-500">{errors.email}</div>}
      <br />
      {/* ... */}
    </form>
  );
};

export default AddUser;
