import axios from 'axios';
import React, { useState } from 'react';
import UserProfile from './user_profile';

const AddUser = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [first_name, setFirst_name] = useState('');
    const [last_name, setLast_name] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser(prevUser => ({ ...prevUser, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Handling...")
      if (!email || password || first_name || last_name) {
        alert('Please fill in all required fields.');
        return;
      }
      const user = { email, password, first_name, last_name };
    await axios.post('http://localhost:5000/users/add', user, {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json',
      },
      body: JSON.stringify(user),
    })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <><form onSubmit={handleSubmit}>
      <h1>Add User</h1>
      <input type="email" name="email" value={user.email} onChange={handleChange} placeholder="Email" />
      <input type="password" name="password" value={user.password} onChange={handleChange} placeholder="Password" />
      <input type="text" name="first_name" value={user.first_name} onChange={handleChange} placeholder="First Name" />
      <input type="text" name="last_name" value={user.last_name} onChange={handleChange} placeholder="Last Name" />
      <button type="submit">Submit</button>
    </form><UserProfile /></>
  );
};

export default AddUser;
