import axios from 'axios';
import React, { useState } from 'react';

const AddUser = () => {
    const [user, setUser] = useState({
        email: '',
        password: '',
        first_name: '',
        last_name: ''
    });
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser(prevUser => ({ ...prevUser, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!user.email || !user.password || !user.first_name || !user.last_name) {
            alert('Please fill in all required fields.');
            return;
        }
        setLoading(true);
        try {
            const response = axios.post('http://localhost:5000/users/add', user);
            console.log('Success:', response.data);
        } catch (error) {
            console.error('Axios Error:', error.message);
            console.error('Server Response:', error.response.data);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='flex flex-col max-w-md mx-auto bg-gray-500 rounded-xl shadow-md overflow-hidden md:max-w-2xl m-8 p-10 mt-2 h-1/2'>
            <div className='flex m-4 p-4'>
                <div>
                    <h1 className=''>Add User</h1><br />
                </div>
                <div>

                </div>
            </div>
        </div>
    );
};

export default AddUser;
