import React from "react";

function Tips() {
	return (
		<div className='flex flex-col max-w-md mx-auto bg-gray-500 rounded-xl shadow-md overflow-hidden md:max-w-2xl m-8 p-10 mt-2 h-1/2'>
			<span>Here are some tips to help you make the most out of your to-do list app:</span>
			<ul>
				<li><b>Prioritize Tasks:</b> Start by listing your tasks in order of importance. This helps you focus on what needs to be done first.</li>
				<li><b>Set Deadlines:</b> Assign due dates to your tasks to keep yourself on track and avoid procrastination</li>
				<li><b>Break Down Tasks:</b> Divide larger tasks into smaller, manageable steps. This makes them less overwhelming and easier to complete.</li>
			</ul>
		</div>
	)
}

export default Tips;
