import axios from 'axios';
import React, { useState } from 'react';

const AddUser = () => {
    const [user, setUser] = useState({
        email: '',
        password: '',
        first_name: '',
        last_name: ''
    });
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser(prevUser => ({ ...prevUser, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!user.email || !user.password || !user.first_name || !user.last_name) {
            alert('Please fill in all required fields.');
            return;
        }
        setLoading(true);
        try {
            const response = axios.post('http://localhost:5000/users/add', user);
            console.log('Success:', response.data);
        } catch (error) {
            console.error('Axios Error:', error.message);
            console.error('Server Response:', error.response.data);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='flex flex-col max-w-md mx-auto bg-gray-500 rounded-xl shadow-md overflow-hidden md:max-w-2xl m-8 p-10 mt-2 h-1/2'>
            <div className='flex m-4 p-4'>
                <h1 className='mr-'>Add User</h1>
                <form onSubmit={handleSubmit}>
                    <label>Email:</label><br />
                    <input type="email" name="email" value={user.email} onChange={handleChange} /><br />
                    <label>Password:</label><br />
                    <input type="password" name="password" value={user.password} onChange={handleChange} /><br />
                    <label>First Name:</label><br />
                    <input type="text" name="first_name" value={user.first_name} onChange={handleChange} /><br />
                    <label>Last Name:</label><br />
                    <input type="text" name="last_name" value={user.last_name} onChange={handleChange} /><br />
                    <button type="submit" disabled={loading}>{loading ? 'Loading...' : 'Submit'}</button>
                </form>
            </div>
        </div>
    );
};

export default AddUser;
