import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function TaskList() {
    const { lastName } = useParams();
    const [tasks, setTasks] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchTasks = async () => {
            setIsLoading(true);
            try {
                const response = await fetch(`/tasks/all`);
                const data = await response.json();
                setTasks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setIsLoading(false);
            }
        };
        fetchTasks();
    }, [lastName]);

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className='flex flex-col max-w-md mx-auto bg-gray-500 rounded-xl shadow-md overflow-hidden md:max-w-2xl m-8 p-10 mt-2 h-1/2'>
            <h1>Task List for {lastName}</h1>
            <ul>
                {tasks.map(task => (
                    <li className='text-black' key={task.id}>{task.title}: {task.description}</li>
                ))}
            </ul>
        </div>
    );
}

export default TaskList;
